<?php if (! defined ( 'BASEPATH' ))	exit ( 'No direct script access allowed' );/** * 分类控制器 * @author chaituan@126.com */class AdminConfigTab extends MY_Model {		function __construct() {		parent::__construct ();		$this->table_name = 'system_config_tab';	}		function getAll($type = 0){		$where['status'] = 1;		if($type>-1)$where['type'] = $type;		return $this->getItems($where);	}}